
public class PracticePattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i;
		for(i=0;i<3;i++)///i=3,2
		{
			for(int k=0;k<5;k++)
			{
				System.out.print("  ");                  //
                
				for(int j=0;j<4;j++)///j=2                   *  *  *      *
				{                                           //             *    
				System.out.print("*");                  //
				                                        //
				}                                           //
				System.out.println(); 
			 }//* *
		}
		
	}

}
