package looppackage;

public class Print1to10Num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		//variable declaration
		int i;
		
		
		//using for() loop to print 1 to 10 numbers
		//at step i=0----it will initialize the i to 0
		// at step i<=10-----it said that i will have value till 10
		// at step i++----it suggest that i will increment if given condition is satisfy
		for(i=1;i<=10;i++)
		{
			//this line will print 1 to 10 numbers if given condition is satisfy
			System.out.println(i);
		}
	}

}
